import React, { useEffect, useState } from 'react';
import Navbar from '../Navbar/Navbar';
import DoctorCard from '../DoctorCard/DoctorCard';

const Notification = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [doctorData, setDoctorData] = useState([]);
  const [appointmentData, setAppointmentData] = useState(null);

  useEffect(() => {
    const storedUsername = sessionStorage.getItem('email');
    //const storedDoctorData = JSON.parse(localStorage.getItem('doctorData'));
    //const storedAppointmentData = JSON.parse(localStorage.getItem(storedDoctorData?.name));
    const storedAppointments = JSON.parse(localStorage.getItem('storedAppointments'));

    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }

    //if (storedDoctorData) {setDoctorData(storedDoctorData);}

    if (storedAppointments) {
      setAppointmentData(storedAppointments);
    }
    //console.log("storedAppointmentData.doctorName :",storedAppointmentData.doctorName);
    console.log("appointments.doctorName :",storedAppointments); }, []);


  return (
    <div>
      <Navbar ></Navbar>
      {children}
      {isLoggedIn && appointmentData  && (
        <>
          <div className="appointment-card">
            <div className="appointment-card__content">
              <h3 className="appointment-card__title">Appointment Details</h3>
              <p className="appointment-card__message">
                <strong>Doctor:</strong> {appointmentData?.name}
              </p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Notification;
