import React, { useEffect, useState } from 'react';
import Navbar from '../Navbar/Navbar';
import './DoctorCard.css';

const Notification = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [doctorData, setDoctorData] = useState(null);
  const [appointmentData, setAppointmentData] = useState(null);

  useEffect(() => {
    const storedUsername = sessionStorage.getItem('email');
    const storedDoctorData = JSON.parse(localStorage.getItem('storedAppointments'));
    const storedAppointmentData = JSON.parse(localStorage.getItem('storedAppointments'));
    console.log("storedAppointmentData:", storedAppointmentData);
    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }

    if (storedDoctorData) {
      setDoctorData(storedDoctorData);
    }

    if (storedAppointmentData) {
        const relevantAppointment = storedAppointmentData[0];
        setAppointmentData(relevantAppointment);
    }
  }, []);
  return (
    <div>
      <Navbar ></Navbar>
      {children}
      {isLoggedIn && appointmentData && (
        <>
          <div className="notification-container">
            <div className="notification-card">
              <h3 className="notification-title">Appointment Details</h3>
              <p className="notification-list">
                <strong>Doctor:</strong> {appointmentData.doctorName}
                <strong>Speciality:</strong> {appointmentData.doctorSpeciality}
                <strong>Name:</strong> {appointmentData.name}
                <strong>Phone Number:</strong> {appointmentData.phoneNumber}
                <strong>Date of Appointment:</strong> {appointmentData.appointmentDate}
                <strong>Time Slot:</strong> {appointmentData.selectedSlot}
              </p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Notification;
