import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ProfileForm from '../ProfileCard/ProfileCard';
import ReportsLayout from '../ReportsLayout/ReportsLayout';
import "./Navbar.css";



const Navbar = () => {
    const [click, setClick] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState("");
    const [email,setEmail]=useState("");
    const [showDropdown, setShowDropdown] = useState(false);
    const [selectedOption, setSelectedOption] = useState(null);
    const handleClick = () => setClick(!click);


    const handleLogout = () => {
        sessionStorage.removeItem("auth-token");
        sessionStorage.removeItem("name");
        sessionStorage.removeItem("email");
        sessionStorage.removeItem("phone");
        localStorage.removeItem("doctorData");
        setIsLoggedIn(false);

        // Remove the reviewFormData from local storage
        for (let i = 0; i < localStorage.length; i++) {
          const key = localStorage.key(i);
          if (key.startsWith("reviewFormData_")) {
            localStorage.removeItem(key);
          }
        }
        setEmail('');
        window.location.reload();
    }
    const handleDropdown = () => {
      setShowDropdown(!showDropdown);
    }
    const handleDropdownOptionClick = (option) => {
        setSelectedOption(option);
        setShowDropdown(false);
    }
    useEffect(() => { 
      const storedemail = sessionStorage.getItem("email");

      if (storedemail) {
            setIsLoggedIn(true);
            setUsername(storedemail);
          }
        }, []);
  return (
    <nav>
      <div className="nav__logo">
        <Link to="/">
        StayHealthy <i style={{color:'#2190FF'}} className="fa fa-user-md"></i></Link>
        <span>.</span>
      </div>
      <div className="nav__icon" onClick={handleClick}>
        <i className={click ? "fa fa-times" : "fa fa-bars"}></i>
      </div>
      <ul className={click ? 'nav__links active' : 'nav__links'}>
        <li className="link">
          <Link to="/">Home</Link>
        </li>
        <li className="link">
          <Link to="/instant-consultation">Appointments</Link>
        </li>
        <li className="link">
          <Link to="/healthblog">Health Blog</Link>
        </li>
        <li className="link">
         <Link to="/reviews">Reviews</Link>
        </li>
        {isLoggedIn?(
          <>
           <li className="link">
            <div className="nav__user-profile" onClick={handleDropdown}>Welcome,{username.split('@')[0]}
            <i className="fa fa-user"></i>
        {showDropdown && (
         <div className="dropdown">
          <div onClick={() => handleDropdownOptionClick("Your Profile")}>Your Profile</div>
          <div onClick={() => handleDropdownOptionClick("Your Reports")}>Your Reports</div>
             </div> )}</div>
           </li>
            <li className="link">
              <button className="btn2" onClick={handleLogout}>
                Logout
              </button>
            </li> {selectedOption === "Your Profile" && (
                    <li className="link">
                        <ProfileForm username={username} email={email} />
                    </li>
                )}
                  {selectedOption === "Your Reports" && (
                    <li className="link">
                <ReportsLayout username={username} email={email}/>
                    </li>
            )}
          </>
        ) : (
          <>
            <li className="link">
            <a href="../Sign_Up/Sign_Up.html">
                <button className="btn1">Sign Up</button>
                </a>
            </li>
            <li className="link">
            <a href="../Login/Login.html">
                <button className="btn1">Login</button>  
                </a>         
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
